  }
}

@layer utilities {
  .crossed-out {
    position: relative;
    opacity: 0.5;
  }

  .crossed-out::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 2px;
    background: hsl(var(--primary));
    transform: translateY(-50%);
  }

  .urgency-crossed {
    position: relative;
    opacity: 0.7;
    background-color: hsl(0 84% 60%);
  }

  .urgency-crossed::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 2px;
    background: hsl(0 100% 40%);
    transform: translateY(-50%);
    z-index: 10;
  }

  .minimap-block {
    height: 4px;
    margin: 1px 0;
    border-radius: 1px;
  }

  .progress-ring {
    transform: rotate(-90deg);
  }

  .urgency-high { 
    color: hsl(0 84% 60%);
  }

  .urgency-medium { 
    color: hsl(25 95% 53%);
  }

  .urgency-low { 
    color: hsl(142 76% 36%);
  }

  .animate-fade-in {
    animation: fadeIn 0.3s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.4s ease-out;
  }

  .animate-pulse-subtle {
    animation: pulseSubtle 2s infinite;
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { transform: translateY(10px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

@keyframes pulseSubtle {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.8; }
}

