import { format, isToday, startOfDay, endOfDay, isSameDay } from "date-fns";
import { CheckCircle, Clock, Play, Circle, Plus } from "lucide-react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Task } from "@shared/schema";
import { calculateDayProgress, formatTimeRange, getUrgencyClass } from "@/lib/time-utils";
import { cn } from "@/lib/utils";

interface DayViewProps {
  tasks: Task[];
  currentDate: Date;
  onTaskUpdate: (taskId: string, updates: Partial<Task>) => void;
  onAddTask: () => void;
}

export function DayView({ tasks, currentDate, onTaskUpdate, onAddTask }: DayViewProps) {
  const dayProgress = calculateDayProgress(currentDate);
  const dayTasks = tasks
    .filter(task => isSameDay(new Date(task.startTime), currentDate))
    .sort((a, b) => new Date(a.startTime).getTime() - new Date(b.startTime).getTime());

  const completedTasks = dayTasks.filter(task => task.completed);
  const isCurrentDay = isToday(currentDate);

  const toggleTaskCompletion = (taskId: string, completed: boolean) => {
    onTaskUpdate(taskId, { completed: !completed });
  };

  const getCurrentTask = () => {
    if (!isCurrentDay) return null;
    const now = new Date();
    return dayTasks.find(task => 
      !task.completed && 
      new Date(task.startTime) <= now && 
      new Date(task.endTime) >= now
    );
  };

  const currentTask = getCurrentTask();

  return (
    <div className="space-y-8" data-testid="day-view">
      {/* Day Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-3xl font-bold text-foreground" data-testid="day-title">
              {format(currentDate, "EEEE, MMMM d, yyyy")}
            </h2>
            <p className="text-muted-foreground mt-1" data-testid="day-summary">
              {completedTasks.length} of {dayTasks.length} tasks completed
            </p>
          </div>
          <div className="flex items-center space-x-4">
            {isCurrentDay && (
              <>
                <div className="flex items-center space-x-2">
                  <Progress value={dayProgress.percentage} className="w-32" data-testid="day-progress" />
                  <span className="text-sm font-medium text-muted-foreground">
                    {Math.round(dayProgress.percentage)}% elapsed
                  </span>
                </div>
                <div className="text-right">
                  <div className="text-sm text-muted-foreground">Time Remaining</div>
                  <div className={`text-lg font-semibold ${getUrgencyClass(dayProgress.urgencyLevel)}`} data-testid="time-remaining">
                    {Math.round(dayProgress.remaining)}%
                  </div>
                </div>
              </>
            )}
            <Button onClick={onAddTask} className="flex items-center space-x-2" data-testid="button-add-task">
              <Plus className="w-4 h-4" />
              <span>Add Task</span>
            </Button>
          </div>
        </div>
      </div>

      {/* Current Task Highlight */}
      {currentTask && (
        <Card className="border-2 border-primary bg-accent" data-testid="current-task-card">
          <CardHeader>
            <div className="flex items-center space-x-3">
              <Play className="text-primary w-5 h-5" />
              <h3 className="text-lg font-semibold text-foreground">Currently Working On</h3>
              <Badge className="bg-primary text-primary-foreground">In Progress</Badge>
            </div>
          </CardHeader>
          <CardContent>
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium text-foreground" data-testid="current-task-title">{currentTask.title}</p>
                {currentTask.description && (
                  <p className="text-sm text-muted-foreground mt-1" data-testid="current-task-description">
                    {currentTask.description}
                  </p>
                )}
              </div>
              <div className="text-right">
                <p className="text-sm text-muted-foreground" data-testid="current-task-time">
                  {formatTimeRange(new Date(currentTask.startTime), new Date(currentTask.endTime))}
                </p>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => toggleTaskCompletion(currentTask.id, currentTask.completed || false)}
                  className="mt-2"
                  data-testid="button-complete-current"
                >
                  Mark Complete
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Tasks List */}
      <Card data-testid="tasks-list-card">
        <CardHeader>
          <h3 className="text-lg font-semibold text-foreground">
            {isCurrentDay ? "Today's Schedule" : `Schedule for ${format(currentDate, "EEEE")}`}
          </h3>
        </CardHeader>
        <CardContent>
          {dayTasks.length === 0 ? (
            <div className="text-center py-12" data-testid="no-tasks">
              <Clock className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
              <h3 className="text-lg font-medium text-foreground mb-2">No tasks scheduled</h3>
              <p className="text-muted-foreground mb-4">Add some tasks to get started with your day</p>
              <Button onClick={onAddTask} data-testid="button-add-first-task">
                <Plus className="w-4 h-4 mr-2" />
                Add Your First Task
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {dayTasks.map((task, index) => {
                const isTaskCompleted = task.completed;
                const isTaskCurrent = task.id === currentTask?.id;
                const taskTime = new Date(task.startTime);
                const now = new Date();
                const isTaskPast = taskTime < now && !isCurrentDay;

                return (
                  <div
                    key={task.id}
                    className={cn(
                      "flex items-center space-x-4 p-4 rounded-lg border transition-all",
                      isTaskCompleted && "opacity-70 bg-muted/30",
                      isTaskCurrent && "bg-primary-lighter border-primary",
                      isTaskPast && "opacity-60",
                      !isTaskCompleted && !isTaskCurrent && !isTaskPast && "hover:bg-muted/50"
                    )}
                    data-testid={`task-${index}`}
                  >
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => toggleTaskCompletion(task.id, task.completed || false)}
                      className="p-0 h-auto"
                      data-testid={`task-toggle-${index}`}
                    >
                      {isTaskCompleted ? (
                        <CheckCircle className="text-green-500 w-5 h-5" />
                      ) : isTaskCurrent ? (
                        <Play className="text-primary w-5 h-5" />
                      ) : (
                        <Circle className="text-gray-400 w-5 h-5" />
                      )}
                    </Button>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center space-x-2">
                        <h4 
                          className={cn(
                            "font-medium text-foreground",
                            isTaskCompleted && "line-through"
                          )}
                          data-testid={`task-title-${index}`}
                        >
                          {task.title}
                        </h4>
                        {task.priority === 'high' && (
                          <Badge variant="destructive" className="text-xs">High Priority</Badge>
                        )}
                        {isTaskCurrent && (
                          <Badge className="text-xs bg-primary text-primary-foreground">Active</Badge>
                        )}
                      </div>

                      {task.description && (
                        <p className="text-sm text-muted-foreground mt-1" data-testid={`task-description-${index}`}>
                          {task.description}
                        </p>
                      )}
                    </div>

                    <div className="text-right">
                      <p className="text-sm font-medium text-foreground" data-testid={`task-time-${index}`}>
                        {formatTimeRange(new Date(task.startTime), new Date(task.endTime))}
                      </p>
                      <p className="text-xs text-muted-foreground">
                        {format(new Date(task.startTime), "h:mm a")}
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Day Summary */}
      {dayTasks.length > 0 && (
        <Card data-testid="day-summary-card">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-foreground mb-4">Day Summary</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600" data-testid="summary-completed">
                  {completedTasks.length}
                </div>
                <div className="text-sm text-muted-foreground">Completed</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-primary" data-testid="summary-remaining">
                  {dayTasks.length - completedTasks.length}
                </div>
                <div className="text-sm text-muted-foreground">Remaining</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-muted-foreground" data-testid="summary-percentage">
                  {dayTasks.length > 0 ? Math.round((completedTasks.length / dayTasks.length) * 100) : 0}%
                </div>
                <div className="text-sm text-muted-foreground">Progress</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/calendar/month-vie.content:
import { format, startOfMonth, endOfMonth, eachWeekOfInterval, eachDayOfInterval, startOfWeek, endOfWeek, isSameMonth, isSameDay, isToday, isPast } from "date-fns";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Task } from "@shared/schema";
import { calculateMonthProgress, getUrgencyClass } from "@/lib/time-utils";
import { cn } from "@/lib/utils";

interface MonthViewProps {
  tasks: Task[];
  currentDate: Date;
  onDateClick: (date: Date) => void;
}

export function MonthView({ tasks, currentDate, onDateClick }: MonthViewProps) {
  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const monthProgress = calculateMonthProgress(currentDate);

  const weeks = eachWeekOfInterval(
    { start: monthStart, end: monthEnd },
    { weekStartsOn: 1 }
  );

  const getTasksForDay = (day: Date) => {
    return tasks.filter(task => isSameDay(new Date(task.startTime), day));
  };

  const getDayStatus = (day: Date) => {
    if (!isSameMonth(day, currentDate)) return 'outside';
    if (isToday(day)) return 'today';
    if (isPast(day)) return 'past';
    return 'future';
  };

  const getCompletedTasksCount = (day: Date) => {
    const dayTasks = getTasksForDay(day);
    return dayTasks.filter(task => task.completed).length;
  };

  const completedTasks = tasks.filter(task => task.completed);
  const totalTasks = tasks.length;

  return (
    <div className="space-y-8" data-testid="month-view">
      {/* Month Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-2xl font-bold text-foreground" data-testid="month-title">
              {format(currentDate, "MMMM yyyy")}
            </h2>
            <p className="text-muted-foreground mt-1" data-testid="month-summary">
              {Math.floor(monthProgress.elapsed)} days completed • {Math.ceil(monthProgress.remaining)} days remaining
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <Progress value={monthProgress.percentage} className="w-32" data-testid="month-progress" />
              <span className="text-sm font-medium text-muted-foreground">
                {Math.round(monthProgress.percentage)}% complete
              </span>
            </div>
            <div className="text-right">
              <div className="text-sm text-muted-foreground">Time Remaining</div>
              <div className={`text-lg font-semibold ${getUrgencyClass(monthProgress.urgencyLevel)}`} data-testid="time-remaining">
                {Math.round(monthProgress.remaining)}%
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Calendar Grid */}
      <Card data-testid="calendar-grid">
        <CardHeader>
          <div className="grid grid-cols-7 gap-2 text-center">
            {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map(day => (
              <div key={day} className="p-2 text-sm font-medium text-muted-foreground">
                {day}
              </div>
            ))}
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            {weeks.map((week, weekIndex) => {
              const weekDays = eachDayOfInterval({
                start: startOfWeek(week, { weekStartsOn: 1 }),
                end: endOfWeek(week, { weekStartsOn: 1 })
              });

              return (
                <div key={weekIndex} className="grid grid-cols-7 gap-2">
                  {weekDays.map((day, dayIndex) => {
                    const dayTasks = getTasksForDay(day);
                    const dayStatus = getDayStatus(day);
                    const completedDayTasks = getCompletedTasksCount(day);
                    const isCurrentDay = isToday(day);
                    const isDayPast = dayStatus === 'past';
                    const isOutsideMonth = dayStatus === 'outside';

                    return (
                      <button
                        key={dayIndex}
                        onClick={() => onDateClick(day)}
                        className={cn(
                          "p-3 min-h-[100px] rounded-lg border text-left transition-all hover:border-primary/50",
                          isOutsideMonth && "opacity-30",
                          isDayPast && !isOutsideMonth && "crossed-out bg-muted/30",
                          isCurrentDay && "border-2 border-primary bg-accent",
                          !isCurrentDay && !isDayPast && !isOutsideMonth && "hover:bg-muted/50"
                        )}
                        data-testid={`calendar-day-${weekIndex}-${dayIndex}`}
                      >
                        <div className="flex items-center justify-between mb-2">
                          <span className={cn(
                            "text-sm font-medium",
                            isOutsideMonth ? "text-muted-foreground" : "text-foreground",
                            isCurrentDay && "font-bold"
                          )}>
                            {format(day, 'd')}
                          </span>
                          {dayTasks.length > 0 && (
                            <Badge 
                              variant={completedDayTasks === dayTasks.length ? "default" : "outline"}
                              className="text-xs px-1 py-0"
                            >
                              {completedDayTasks}/{dayTasks.length}
                            </Badge>
                          )}
                        </div>

                        <div className="space-y-1">
                          {dayTasks.slice(0, 3).map((task, taskIndex) => (
                            <div
                              key={task.id}
                              className={cn(
                                "text-xs p-1 rounded truncate",
                                task.completed 
                                  ? "bg-green-100 text-green-700 line-through" 
                                  : task.priority === 'high'
                                  ? "bg-red-100 text-red-700"
                                  : task.priority === 'medium'
                                  ? "bg-primary-lighter text-primary"
                                  : "bg-muted text-muted-foreground"
                              )}
                              title={task.title}
                              data-testid={`task-preview-${weekIndex}-${dayIndex}-${taskIndex}`}
                            >
                              {task.title}
                            </div>
                          ))}
                          {dayTasks.length > 3 && (
                            <div className="text-xs text-muted-foreground">
                              +{dayTasks.length - 3} more
                            </div>
                          )}
                        </div>
                      </button>
                    );
                  })}
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Month Summary */}
      <Card data-testid="month-summary-card">
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">Month Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600" data-testid="summary-completed">
                {completedTasks.length}
              </div>
              <div className="text-sm text-muted-foreground">Tasks Completed</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary" data-testid="summary-remaining">
                {totalTasks - completedTasks.length}
              </div>
              <div className="text-sm text-muted-foreground">Tasks Remaining</div>
            </div>
            <div className="text-center">
              <div className={`text-2xl font-bold ${getUrgencyClass(monthProgress.urgencyLevel)}`} data-testid="summary-productivity">
                {Math.round(monthProgress.percentage)}%
              </div>
              <div className="text-sm text-muted-foreground">Month Progress</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-muted-foreground" data-testid="summary-efficiency">
                {totalTasks > 0 ? Math.round((completedTasks.length / totalTasks) * 100) : 0}%
              </div>
              <div className="text-sm text-muted-foreground">Task Completion</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/calendar/sidebar.t.content:
import { format } from "date-fns";
import { CalendarIcon } from "lucide-react";
import { ProgressRing } from "@/components/ui/progress-ring";
import { UrgencyMinimap } from "@/components/ui/urgency-minimap";
import { calculateDayProgress, calculateWeekProgress, getUrgencyClass } from "@/lib/time-utils";
import { Task } from "@shared/schema";
import { useEffect, useState } from "react";

interface SidebarProps {
  tasks: Task[];
  currentView: 'day' | 'week' | 'month' | 'year';
}

export function Sidebar({ tasks, currentView }: SidebarProps) {
  const [currentTime, setCurrentTime] = useState(new Date());
  const dayProgress = calculateDayProgress();
  const weekProgress = calculateWeekProgress();

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Update every minute

    return () => clearInterval(timer);
  }, []);

  const completedTasks = tasks.filter(task => task.completed);
  const totalTasks = tasks.length;
  const timeUtilized = weekProgress.percentage;

  return (
    <div className="w-80 bg-surface border-r border-border flex flex-col" data-testid="sidebar">
      {/* Header */}
      <div className="p-6 border-b border-border">
        <div className="flex items-center space-x-3 mb-4">
          <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
            <CalendarIcon className="text-primary-foreground text-sm" />
          </div>
          <h1 className="text-xl font-bold text-foreground" data-testid="app-title">TimeFlow</h1>
        </div>

        {/* Current Time & Date */}
        <div className="text-sm text-muted-foreground mb-2" data-testid="current-datetime">
          {format(currentTime, "EEEE, MMMM d, yyyy • h:mm a")}
        </div>

        {/* Progress Ring - Current Day */}
        <div className="flex items-center space-x-3 mb-4">
          <ProgressRing progress={dayProgress.percentage} size={48} />
          <div>
            <div className="text-sm font-medium text-foreground" data-testid="day-progress-label">Day Progress</div>
            <div className={`text-xs ${getUrgencyClass(dayProgress.urgencyLevel)}`} data-testid="day-progress-remaining">
              {Math.round(dayProgress.remaining)}% remaining
            </div>
          </div>
        </div>
      </div>

      {/* Urgency Minimap Section */}
      <div className="p-6 border-b border-border">
        <UrgencyMinimap tasks={tasks} />
      </div>

      {/* Quick Stats */}
      <div className="p-6">
        <h3 className="text-sm font-semibold text-foreground mb-3" data-testid="stats-title">Week Statistics</h3>
        <div className="space-y-3">
          <div className="flex justify-between items-center">
            <span className="text-sm text-muted-foreground">Tasks Completed</span>
            <span className="text-sm font-semibold text-green-600" data-testid="tasks-completed">
              {completedTasks.length}/{totalTasks}
            </span>
          </div>
          <div className="flex justify-between items-center">
            <span className="text-sm text-muted-foreground">Time Utilized</span>
            <span className="text-sm font-semibold text-primary" data-testid="time-utilized">
              {Math.round(timeUtilized)}%
            </span>
          </div>
          <div className="flex justify-between items-center">
            <span className="text-sm text-muted-foreground">Days Remaining</span>
            <span className={`text-sm font-semibold ${getUrgencyClass(weekProgress.urgencyLevel)}`} data-testid="days-remaining">
              {(7 - Math.floor(weekProgress.elapsed / 100 * 7)).toFixed(1)} days
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/calendar/task-form.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertTaskSchema, type InsertTask } from "@shared/schema";
import { format } from "date-fns";

interface TaskFormProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSubmit: (task: InsertTask) => void;
  defaultDate?: Date;
}

export function TaskForm({ open, onOpenChange, onSubmit, defaultDate = new Date() }: TaskFormProps) {
  const form = useForm<InsertTask>({
    resolver: zodResolver(insertTaskSchema),
    defaultValues: {
      title: "",
      description: "",
      startTime: new Date(defaultDate.getTime()),
      endTime: new Date(defaultDate.getTime() + 60 * 60 * 1000), // 1 hour later
      completed: false,
      priority: "medium",
    },
  });

  const handleSubmit = (data: InsertTask) => {
    onSubmit(data);
    form.reset();
    onOpenChange(false);
  };

  const formatDateTimeLocal = (date: Date) => {
    const offset = date.getTimezoneOffset();
    const localDate = new Date(date.getTime() - offset * 60 * 1000);
    return localDate.toISOString().slice(0, 16);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]" data-testid="task-form-dialog">
        <DialogHeader>
          <DialogTitle data-testid="task-form-title">Add New Task</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4" data-testid="task-form">
            <FormField
              control={form.control}
              name="title"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Title</FormLabel>
                  <FormControl>
                    <Input placeholder="Enter task title" {...field} data-testid="input-title" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Enter task description (optional)" 
                      {...field} 
                      value={field.value || ""}
                      data-testid="input-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="priority"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Priority</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value} data-testid="select-priority">
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select priority" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="low">Low</SelectItem>
                      <SelectItem value="medium">Medium</SelectItem>
                      <SelectItem value="high">High</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="startTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Start Time</FormLabel>
                    <FormControl>
                      <Input
                        type="datetime-local"
                        value={formatDateTimeLocal(new Date(field.value))}
                        onChange={(e) => field.onChange(new Date(e.target.value))}
                        data-testid="input-start-time"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="endTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>End Time</FormLabel>
                    <FormControl>
                      <Input
                        type="datetime-local"
                        value={formatDateTimeLocal(new Date(field.value))}
                        onChange={(e) => field.onChange(new Date(e.target.value))}
                        data-testid="input-end-time"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="flex justify-end space-x-2">
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button type="submit" data-testid="button-submit">Add Task</Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/calendar/week-view.content:
import { format, startOfWeek, endOfWeek, eachDayOfInterval, isSameDay, isToday, isPast } from "date-fns";
import { CheckCircle, Clock, Play, Circle } from "lucide-react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Task } from "@shared/schema";
import { calculateWeekProgress, formatTimeRange, getUrgencyClass } from "@/lib/time-utils";
import { cn } from "@/lib/utils";

interface WeekViewProps {
  tasks: Task[];
  currentDate: Date;
  onTaskUpdate: (taskId: string, updates: Partial<Task>) => void;
}

export function WeekView({ tasks, currentDate, onTaskUpdate }: WeekViewProps) {
  const weekStart = startOfWeek(currentDate, { weekStartsOn: 1 });
  const weekEnd = endOfWeek(currentDate, { weekStartsOn: 1 });
  const weekDays = eachDayOfInterval({ start: weekStart, end: weekEnd });
  const weekProgress = calculateWeekProgress(currentDate);

  const completedTasks = tasks.filter(task => task.completed);
  const totalTasks = tasks.length;

  const getTasksForDay = (day: Date) => {
    return tasks
      .filter(task => isSameDay(new Date(task.startTime), day))
      .sort((a, b) => new Date(a.startTime).getTime() - new Date(b.startTime).getTime());
  };

  const getDayStatus = (day: Date) => {
    const dayTasks = getTasksForDay(day);
    const completedDayTasks = dayTasks.filter(task => task.completed);

    if (isToday(day)) return 'current';
    if (isPast(day)) return 'completed';
    return 'planned';
  };

  const getDayProgress = (day: Date) => {
    const dayTasks = getTasksForDay(day);
    if (dayTasks.length === 0) return 0;

    const completed = dayTasks.filter(task => task.completed).length;
    return (completed / dayTasks.length) * 100;
  };

  const toggleTaskCompletion = (taskId: string, completed: boolean) => {
    onTaskUpdate(taskId, { completed: !completed });
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return <CheckCircle className="text-green-500" />;
      case 'current': return <Clock className="text-primary animate-pulse-subtle" />;
      default: return <Circle className="text-gray-400" />;
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'completed': return <Badge className="bg-green-100 text-green-700">Completed</Badge>;
      case 'current': return <Badge className="bg-primary text-primary-foreground">Today</Badge>;
      default: return <Badge variant="outline">Planned</Badge>;
    }
  };

  return (
    <div className="space-y-8" data-testid="week-view">
      {/* Week Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-2xl font-bold text-foreground" data-testid="week-title">
              Week of {format(weekStart, "MMMM d")} - {format(weekEnd, "d, yyyy")}
            </h2>
            <p className="text-muted-foreground mt-1" data-testid="week-summary">
              {Math.floor(weekProgress.elapsed / 100 * 7)} days completed • {7 - Math.floor(weekProgress.elapsed / 100 * 7)} days remaining
            </p>
          </div>
          <div className="flex items-center space-x-4">
            {/* Week Progress */}
            <div className="flex items-center space-x-2">
              <Progress value={weekProgress.percentage} className="w-24" data-testid="week-progress" />
              <span className="text-sm font-medium text-muted-foreground">
                {Math.round(weekProgress.percentage)}% complete
              </span>
            </div>
            <div className="text-right">
              <div className="text-sm text-muted-foreground">Time Remaining</div>
              <div className={`text-lg font-semibold ${getUrgencyClass(weekProgress.urgencyLevel)}`} data-testid="time-remaining">
                {Math.round(weekProgress.remaining)}%
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Days List */}
      <div className="space-y-6">
        {weekDays.map((day, dayIndex) => {
          const dayTasks = getTasksForDay(day);
          const dayStatus = getDayStatus(day);
          const dayProgressPercent = getDayProgress(day);
          const isCurrentDay = isToday(day);
          const isDayCompleted = dayStatus === 'completed';

          return (
            <Card 
              key={dayIndex} 
              className={cn(
                "overflow-hidden transition-all duration-200",
                isDayCompleted && "crossed-out",
                isCurrentDay && "border-2 border-primary shadow-md"
              )}
              data-testid={`day-card-${dayIndex}`}
            >
              <CardHeader className={cn(
                "border-b",
                isDayCompleted && "bg-muted/50",
                isCurrentDay && "bg-accent"
              )}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    {getStatusIcon(dayStatus)}
                    <h3 className="text-lg font-semibold text-foreground" data-testid={`day-title-${dayIndex}`}>
                      {format(day, "EEEE, MMM d")}
                    </h3>
                    {getStatusBadge(dayStatus)}
                  </div>
                  <div className="text-sm text-muted-foreground" data-testid={`day-progress-${dayIndex}`}>
                    {dayStatus === 'completed' ? '100%' : Math.round(dayProgressPercent) + '%'} • {dayTasks.length} tasks
                  </div>
                </div>
              </CardHeader>

              <CardContent className="p-6">
                {dayTasks.length === 0 ? (
                  <div className="text-center py-8 text-muted-foreground" data-testid={`no-tasks-${dayIndex}`}>
                    No tasks scheduled for this day
                  </div>
                ) : (
                  <div className="space-y-3">
                    {dayTasks.map((task, taskIndex) => {
                      const isTaskCompleted = task.completed;
                      const isTaskCurrent = isCurrentDay && !isTaskCompleted && new Date() >= new Date(task.startTime) && new Date() <= new Date(task.endTime);

                      return (
                        <div
                          key={task.id}
                          className={cn(
                            "flex items-center space-x-3 p-3 rounded-lg transition-all",
                            isTaskCompleted && "opacity-70",
                            isTaskCurrent && "bg-primary-lighter",
                            !isTaskCompleted && !isTaskCurrent && "hover:bg-muted/50"
                          )}
                          data-testid={`task-${dayIndex}-${taskIndex}`}
                        >
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => toggleTaskCompletion(task.id, task.completed || false)}
                            className="p-0 h-auto"
                            data-testid={`task-toggle-${dayIndex}-${taskIndex}`}
                          >
                            {isTaskCompleted ? (
                              <CheckCircle className="text-green-500 w-4 h-4" />
                            ) : isTaskCurrent ? (
                              <Play className="text-primary w-4 h-4" />
                            ) : (
                              <Circle className="text-gray-400 w-4 h-4" />
                            )}
                          </Button>

                          <span 
                            className={cn(
                              "flex-1 text-foreground",
                              isTaskCompleted && "line-through"
                            )}
                            data-testid={`task-title-${dayIndex}-${taskIndex}`}
                          >
                            {task.title}
                          </span>

                          <span className="text-xs text-muted-foreground" data-testid={`task-time-${dayIndex}-${taskIndex}`}>
                            {formatTimeRange(new Date(task.startTime), new Date(task.endTime))}
                          </span>

                          {task.priority === 'high' && (
                            <Badge variant="destructive" className="text-xs">High</Badge>
                          )}

                          {isTaskCurrent && (
                            <Badge className="text-xs bg-primary text-primary-foreground">In Progress</Badge>
                          )}
                        </div>
                      );
                    })}
                  </div>
                )}
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Week Summary */}
      <Card data-testid="week-summary-card">
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">Week Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600" data-testid="summary-completed">
                {completedTasks.length}
              </div>
              <div className="text-sm text-muted-foreground">Tasks Completed</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary" data-testid="summary-remaining">
                {totalTasks - completedTasks.length}
              </div>
              <div className="text-sm text-muted-foreground">Tasks Remaining</div>
            </div>
            <div className="text-center">
              <div className={`text-2xl font-bold ${getUrgencyClass(weekProgress.urgencyLevel)}`} data-testid="summary-productivity">
                {Math.round(weekProgress.percentage)}%
              </div>
              <div className="text-sm text-muted-foreground">Week Productivity</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/calendar/year-view.content:
import { format, startOfYear, endOfYear, eachMonthOfInterval, startOfMonth, endOfMonth, isSameMonth, isThisMonth, isPast } from "date-fns";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Task } from "@shared/schema";
import { calculateYearProgress, getUrgencyClass } from "@/lib/time-utils";
import { cn } from "@/lib/utils";

interface YearViewProps {
  tasks: Task[];
  currentDate: Date;
  onMonthClick: (date: Date) => void;
}

export function YearView({ tasks, currentDate, onMonthClick }: YearViewProps) {
  const yearStart = startOfYear(currentDate);
  const yearEnd = endOfYear(currentDate);
  const yearProgress = calculateYearProgress(currentDate);

  const months = eachMonthOfInterval({ start: yearStart, end: yearEnd });

  const getTasksForMonth = (month: Date) => {
    const monthStart = startOfMonth(month);
    const monthEnd = endOfMonth(month);
    return tasks.filter(task => {
      const taskDate = new Date(task.startTime);
      return taskDate >= monthStart && taskDate <= monthEnd;
    });
  };

  const getMonthStatus = (month: Date) => {
    if (isThisMonth(month)) return 'current';
    if (isPast(endOfMonth(month))) return 'completed';
    return 'future';
  };

  const getCompletedTasksForMonth = (month: Date) => {
    const monthTasks = getTasksForMonth(month);
    return monthTasks.filter(task => task.completed).length;
  };

  const completedTasks = tasks.filter(task => task.completed);
  const totalTasks = tasks.length;

  return (
    <div className="space-y-8" data-testid="year-view">
      {/* Year Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-2xl font-bold text-foreground" data-testid="year-title">
              {format(currentDate, "yyyy")}
            </h2>
            <p className="text-muted-foreground mt-1" data-testid="year-summary">
              {Math.floor(yearProgress.elapsed / 100 * 12)} months completed • {12 - Math.floor(yearProgress.elapsed / 100 * 12)} months remaining
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <Progress value={yearProgress.percentage} className="w-40" data-testid="year-progress" />
              <span className="text-sm font-medium text-muted-foreground">
                {Math.round(yearProgress.percentage)}% complete
              </span>
            </div>
            <div className="text-right">
              <div className="text-sm text-muted-foreground">Time Remaining</div>
              <div className={`text-lg font-semibold ${getUrgencyClass(yearProgress.urgencyLevel)}`} data-testid="time-remaining">
                {Math.round(yearProgress.remaining)}%
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Months Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6" data-testid="months-grid">
        {months.map((month, monthIndex) => {
          const monthTasks = getTasksForMonth(month);
          const monthStatus = getMonthStatus(month);
          const completedMonthTasks = getCompletedTasksForMonth(month);
          const isCurrentMonth = isThisMonth(month);
          const isMonthCompleted = monthStatus === 'completed';

          return (
            <Card
              key={monthIndex}
              className={cn(
                "cursor-pointer transition-all hover:shadow-md",
                isMonthCompleted && "crossed-out opacity-75",
                isCurrentMonth && "border-2 border-primary bg-accent"
              )}
              onClick={() => onMonthClick(month)}
              data-testid={`month-card-${monthIndex}`}
            >
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-semibold text-foreground" data-testid={`month-title-${monthIndex}`}>
                    {format(month, "MMMM")}
                  </h3>
                  {isCurrentMonth && (
                    <Badge className="bg-primary text-primary-foreground text-xs">Current</Badge>
                  )}
                  {isMonthCompleted && (
                    <Badge className="bg-green-100 text-green-700 text-xs">Done</Badge>
                  )}
                </div>
              </CardHeader>

              <CardContent>
                <div className="space-y-3">
                  {/* Task Summary */}
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-muted-foreground">Tasks</span>
                    <span className="font-medium" data-testid={`month-task-count-${monthIndex}`}>
                      {completedMonthTasks}/{monthTasks.length}
                    </span>
                  </div>

                  {/* Progress Bar */}
                  {monthTasks.length > 0 && (
                    <Progress 
                      value={(completedMonthTasks / monthTasks.length) * 100} 
                      className="h-2" 
                      data-testid={`month-progress-${monthIndex}`}
                    />
                  )}

                  {/* High Priority Tasks */}
                  {monthTasks.filter(task => task.priority === 'high').length > 0 && (
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-red-600">High Priority</span>
                      <span className="font-medium text-red-600" data-testid={`month-high-priority-${monthIndex}`}>
                        {monthTasks.filter(task => task.priority === 'high' && !task.completed).length}
                      </span>
                    </div>
                  )}

                  {/* Recent/Upcoming Tasks Preview */}
                  {monthTasks.length > 0 ? (
                    <div className="space-y-1">
                      {monthTasks.slice(0, 2).map((task, taskIndex) => (
                        <div
                          key={task.id}
                          className={cn(
                            "text-xs p-2 rounded truncate",
                            task.completed 
                              ? "bg-green-100 text-green-700 line-through" 
                              : task.priority === 'high'
                              ? "bg-red-100 text-red-700"
                              : "bg-muted text-muted-foreground"
                          )}
                          title={task.title}
                          data-testid={`month-task-preview-${monthIndex}-${taskIndex}`}
                        >
                          {task.title}
                        </div>
                      ))}
                      {monthTasks.length > 2 && (
                        <div className="text-xs text-muted-foreground text-center">
                          +{monthTasks.length - 2} more tasks
                        </div>
                      )}
                    </div>
                  ) : (
                    <div className="text-xs text-center text-muted-foreground py-2">
                      No tasks scheduled
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Year Summary */}
      <Card data-testid="year-summary-card">
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">Year Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600" data-testid="summary-completed">
                {completedTasks.length}
              </div>
              <div className="text-sm text-muted-foreground">Tasks Completed</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary" data-testid="summary-remaining">
                {totalTasks - completedTasks.length}
              </div>
              <div className="text-sm text-muted-foreground">Tasks Remaining</div>
            </div>
            <div className="text-center">
              <div className={`text-2xl font-bold ${getUrgencyClass(yearProgress.urgencyLevel)}`} data-testid="summary-year-progress">
                {Math.round(yearProgress.percentage)}%
              </div>
              <div className="text-sm text-muted-foreground">Year Progress</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-muted-foreground" data-testid="summary-efficiency">
                {totalTasks > 0 ? Math.round((completedTasks.length / totalTasks) * 100) : 0}%
              </div>
              <div className="text-sm text-muted-foreground">Overall Efficiency</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

